// Minimal worker stub for Tesseract.js in static builds
self.addEventListener('message', function(e) {
  const data = e.data || {};

  // Send back a mock response for recognize
  if (data.action === 'recognize') {
    self.postMessage({
      status: 'done',
      data: {
        text: 'OCR functionality is limited in static builds. Please use the dynamic version for full functionality.'
      }
    });
  } else {
    // For load, loadLanguage, initialize just send success
    self.postMessage({
      status: 'done',
      data: {}
    });
  }
});

console.log('Tesseract.js static worker stub loaded');
